# Enhanced dashboard with template statistics template_stats = [] templates = env['zns.template'].search([('active', '=', True)])
for template in templates: total_usage = template.so_usage_count + template.invoice_usage_count + template.contact_usage_count defaults = [] if template.is_default_for_so: defaults.append('SO') if template.is_default_for_invoice: defaults.append('Invoice') if template.is_default_for_contact: defaults.append('Contact')
template_stats.append({ 'name': template.name, 'template_id': template.template_id, 'type': template.template_type, 'total_usage': total_usage, 'so_usage': template.so_usage_count, 'invoice_usage': template.invoice_usage_count, 'contact_usage': template.contact_usage_count, 'defaults': ', '.join(defaults) if defaults else 'None', 'last_sync': template.last_sync.strftime('%d/%m/%Y') if template.last_sync else 'Never', })
# Sort by total usage template_stats.sort(key=lambda x: x['total_usage'], reverse=True)
