# Simple template check using server action
templates = env['zns.template'].search([])
template_count = len(templates)

if template_count > 0:
    template_list = []
    for t in templates:
        template_list.append("â€¢ " + str(t.name) + " (ID: " + str(t.template_id) + ", Active: " + str(t.active) + ")")

    message = "âœ… Found " + str(template_count) + " templates in database:\n\n" + "\n".join(template_list[:10])
    if template_count > 10:
        message += "\n... and " + str(template_count - 10) + " more"

    message += "\n\nðŸ”§ If you can't see them in menu:\n1. Restart Odoo\n2. Update Apps List\n3. Hard refresh browser (Ctrl+F5)"

    action = {
        'type': 'ir.actions.client',
        'tag': 'display_notification',
        'params': {
            'title': 'Found ' + str(template_count) + ' Templates!',
            'message': message,
            'type': 'success',
            'sticky': True,
        }
    }
else:
    action = {
        'type': 'ir.actions.client',
        'tag': 'display_notification', 
        'params': {
            'title': 'No Templates Found',
            'message': "No templates found in database.\n\nThis means Auto Sync didn't actually create them.\nTry running Auto Sync again or create templates manually.",
            'type': 'warning',
            'sticky': True,
        }
    }
